import React from 'react';

const HeroSection = () => {
  return (
    <div className="relative flex justify-center items-center gap-20 my-16 px-4 pb-32">
      {/* Faded Text */}
      <div className="absolute select-none text-7xl text-gray-200 font-bold left-[-5%] bottom-[-16%] transition-all duration-[3000ms]">
        Faded Text
      </div>

      {/* Left Content */}
      <div className="flex flex-col justify-center gap-8">
        <h1 className="text-3xl text-[#343d68] font-medium">Hero Section Heading</h1>
        <h2 className="text-4xl leading-[45px]">Hero Section Sub Heading</h2>
        <p className="mt-4 w-[70%] font-medium">Hero Section Description</p>
        <button className="bg-[#e84949] text-white px-8 py-3 shadow-lg font-medium text-xl relative z-10 border-3 border-transparent transition-all duration-500 hover:border-[#e84949] hover:text-black">
          Call to Action
        </button>
      </div>

      {/* Right Content with Images and Icons */}
      <div className="relative">
        <div className="p-10 filter grayscale transition-all duration-1000 animate-scaleImage">
          <img src="user-image.jpg" alt="User Image" />
        </div>

        {/* Icon Animations */}
        <div className="absolute bottom-[-1rem] right-0 animate-dotsAnimation">
          {/* Dot icon */}
          <span className="text-white text-3xl">•</span>
        </div>
        <div className="absolute top-[-0.8em] right-4 animate-cubeRotate">
          {/* Cube icon */}
          <span className="text-white text-3xl">■</span>
        </div>
        <div className="absolute left-0 bottom-0 animate-shakeEffect">
          {/* Circle icon */}
          <span className="text-white text-3xl">○</span>
        </div>
        <div className="absolute top-[1.5em] left-[-0.3em] animate-zigzagAnimation">
          {/* Zigzag icon */}
          <span className="text-white text-3xl">≡</span>
        </div>
        <div className="absolute top-[-0.8rem] left-2/4 animate-shakeEffectPlus">
          {/* Plus icon */}
          <span className="text-white text-3xl">+</span>
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
