import React from 'react';

const HeroSection = () => {
  return (
    <div className="hero-section container flex justify-center items-center gap-20 py-16">
      <div className="hero-section-left">
        <h2 className="hero-section-heading text-3xl text-gray-800 font-medium">Hi! jo</h2>
        <h3 className="role text-indigo-600 font-bold text-5xl">Web Developer</h3>
        <p className="hero-section-sub-heading text-5xl text-gray-600">I create amazing web experiences</p>
        <p className="hero-section-description text-lg text-gray-500 mt-4 w-3/4">
          I specialize in building responsive and interactive websites. I work with technologies such as React, Node.js, and more.
        </p>
        <button className="btn-pink mt-8 px-6 py-3">Contact Me</button>
      </div>
      <div className="hero-section-right relative">
        <img src="/images/user-image.jpg" alt="User" className="user-image rounded-full" />
        <div className="icon-dots absolute bottom-[-1rem] right-0 animate-bounce">
          <span className="text-2xl text-gray-700">...</span>
        </div>
      </div>
    </div>
  );
};

export default HeroSection;
