import React from "react";

const projects = [
  {
    id: 1,
    number: "01",
    title: "Tint & Orange",
    description: "It is a car modification company which provides sheets to protect your car from scratches.",
    skills: [
      "./images/stack/HTML.png",
      "./images/stack/CSS.png",
      "./images/stack/Javascript.svg",
      "./images/stack/Express.png",
      "./images/stack/NextJsCircle.png",
      "./images/stack/Tailwind.png",
      "./images/stack/NodeJs.svg",
      "./images/stack/MongoDB.svg",
      "./images/stack/Redux.svg",
      "./images/stack/Vercel.svg"
    ],
    githubLink: "https://github.com/yourrepo",
    liveLink: "https://yourliveprojectlink.com"
  },
  {
    id: 2,
    number: "02",
    title: "Breaking Copyrights",
    description: "A platform to download copyright-free YouTube music.",
    skills: [
      "./images/stack/HTML.png",
      "./images/stack/CSS.png",
      "./images/stack/Javascript.svg",
      "./images/stack/NextJsCircle.png",
      "./images/stack/Tailwind.png",
      "./images/stack/Redux.svg",
      "./images/stack/Vercel.svg"
    ],
    githubLink: "https://github.com/yourrepo",
    liveLink: "https://yourliveprojectlink.com"
  },
  {
    id: 3,
    number: "03",
    title: "Isha Tattva",
    description: "A website for an interior designer to showcase her works and samples.",
    skills: [
      "./images/stack/HTML.png",
      "./images/stack/CSS.png",
      "./images/stack/Javascript.svg",
      "./images/stack/NextJsCircle.png",
      "./images/stack/Tailwind.png",
      "./images/stack/Vercel.svg"
    ],
    githubLink: "https://github.com/yourrepo",
    liveLink: "https://yourliveprojectlink.com"
  },
  {
    id: 4,
    number: "04",
    title: "Granit",
    description: "An architectural website to plan your building or home (Hebrew Language).",
    skills: [
      "./images/stack/NextJsCircle.png",
      "./images/stack/Tailwind.png",
      "./images/stack/Vercel.svg"
    ],
    githubLink: "https://github.com/yourrepo",
    liveLink: "https://yourliveprojectlink.com"
  }
];

const ProjectSection = () => {
  return (
    <div className="project-section bg-gray-200 py-16" id="projects">
      <h2 className="text-5xl font-bold text-center text-orange-600 mb-16">Projects</h2>

      <div className="project-container max-w-screen-xl mx-auto px-8 space-y-28">
        {projects.map((project) => (
          <div
            key={project.id}
            className="project-card relative bg-cover h-[550px] bg-center shadow-xl transition-transform transform group"
            style={{ backgroundImage: `url(./images/projects/Project${project.id}.png)` }}
          >
            {/* Overlay */}
            <div className="absolute top-0 left-0 right-0 bottom-0 bg-black opacity-60 z-0"></div>

            {/* Project Content */}
            <div
              className={`absolute flex flex-col gap-4 p-8 text-white z-10 transition-all group-hover:scale-110 ${project.id % 2 === 0 ? "left-10" : "right-10"}`}
            >
              {/* Skills */}
              <div className="project-skills flex flex-wrap gap-3">
                {project.skills.map((skill, index) => (
                  <img key={index} className="w-10" src={skill} alt={`Skill ${index + 1}`} />
                ))}
              </div>

              {/* Title and Description */}
              <h2 className="text-4xl font-bold">{project.title}</h2>
              <p className="text-xl italic w-3/4">{project.description}</p>

              {/* Buttons */}
              <div className="btn-group flex gap-4">
                <button className="text-lg font-medium bg-orange-600 text-white px-6 py-2 rounded-full shadow-md hover:bg-transparent hover:text-orange-600 hover:border-2 transition">
                  Read More
                </button>
                <a href={project.githubLink} target="_blank" rel="noopener noreferrer">
                  <i title="GitHub Link" className="fa-brands fa-github text-2xl hover:text-orange-600"></i>
                </a>
                <a href={project.liveLink} target="_blank" rel="noopener noreferrer">
                  <i title="Live Link" className="fa-solid fa-link text-2xl hover:text-orange-600"></i>
                </a>
              </div>
            </div>

            {/* Project Number */}
            <div
              className={`absolute text-9xl font-extrabold text-white opacity-0 transition-opacity group-hover:opacity-100 ${project.id % 2 === 0 ? "right-0 top-[-45px]" : "left-0 top-[-45px]"}`}
            >
              {project.number}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProjectSection;
